      ];
      assert: '[{"legalName":"ACME Corp.","employees":[{"firstName":"Road"},{"firstName":"Wile"}]},{"legalName":"Monsters Inc.","employees":[{"firstName":"Jake"},{"firstName":"Mike"}]}]';
