)->toOne().name}: String[1];
  sedol() {$this.synonyms->filter(
  s: relationalMapping::Synonym[1]|$s.type ==
    relationalMapping::ProductSynonymType.SEDOL
)->toOne().name}: String[1];
}

Association relationalMapping::ProdSynonym
{
  product: relationalMapping::Product[1];
