          id,
          createdDate,
          customer{
            companyName
          },
          employee{
            firstName
          },
          lineItems{
            product{
              name,
              category{
                name
              }
            },
            quantity
          },
          shipper{
            name
          }
        }
      }#
    )->serialize(
      #{
